import ListHeader from './components/ListHeader'
import { useEffect, useState} from 'react'
import ListItem from './components/ListItem'

const App = () => {
  const userEmail = "yared@test.com"
  const[ tasks, setTasks] = useState(null)

  const getData = async () => {
    try {
      const response = await fetch ( `http://localhost:4000/todos/${userEmail}`)
      const json = await response.json()
      setTasks(json)
    }catch (err){
      console.error(err)
    }
  }
  useEffect ( ()=> getData, [] )

  console.log(tasks)

  //sort by date 

  const sortedTasks = tasks?.sort((a,b) => new Date (a.date) - new Date(b.date))


  return (
    <div className=" bg-[rgb(233,241,249)] flex items-center justify-center mt-12">
      <div className="app bg-white shadow-md rounded-lg p-4  w-[800px] mt-12">
        <ListHeader listName={'Task manager'} />
        {sortedTasks?.map((task) => <ListItem key={task.id} task={task} />)}
      </div>
    </div>
  );
};

export default App
