import { useState } from 'react';

const Auth = () => {
  const [isLogIn, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState(null);
  console.log(email, password, confirmPassword);

  const viewLogin = (status) => {
    setError(null);
    setIsLogin(status);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!isLogIn && password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    const endpoint = isLogIn ? 'login' : 'signup';
    
    try {
      const response = await fetch(`http://localhost:8000/${endpoint}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.error('Fetch error:', error);
      setError('Failed to fetch');
    }
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      <div className="w-full max-w-md p-8 space-y-4 bg-white shadow-lg rounded-md">
        <form className="space-y-6" onSubmit={handleSubmit}>
          <h2 className="text-2xl font-bold text-center">
            {isLogIn ? 'Please login' : 'Please sign up!'}
          </h2>
          <input
            type="email"
            placeholder="Email"
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:border-green-500"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:border-green-500"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          {!isLogIn && (
            <input
              type="password"
              placeholder="Confirm Password"
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:border-green-500"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
            />
          )}
          <button
            className="w-full py-3 text-white bg-green-500 rounded-lg cursor-pointer hover:bg-green-600"
            type="submit"
          >
            {isLogIn ? 'Login' : 'Sign Up'}
          </button>
          {error && <p className="text-red-500">{error}</p>}
        </form>

        <div className="flex justify-center space-x-4 mt-4">
          <button
            onClick={() => viewLogin(false)}
            className={`px-4 py-2 font-semibold rounded-md ${!isLogIn ? 'bg-white text-green-600' : 'bg-gray-200 text-gray-600'}`}
          >
            Sign Up
          </button>
          <button
            onClick={() => viewLogin(true)}
            className={`px-4 py-2 font-semibold rounded-md ${isLogIn ? 'bg-white text-green-600' : 'bg-gray-200 text-gray-600'}`}
          >
            Login
          </button>
        </div>
      </div>
    </div>
  );
};

export default Auth;
