import { useState } from 'react'

const Modal = ({ mode, setShowModal, getData, task}) => {
  const editMode = mode === 'edit' ? true : false
  
  const [data, setData] = useState({
    user_email: editMode ? task.user_email : 'yared@test.com',
    title:editMode ? task.title : null,
    progress:editMode ? task.progress : 50,
    date:editMode ? "" : new Date()

 } )
 const postData =  async (e) => {
  e.preventDefault()

  try {
   const response =  await fetch('http://localhost:4000/todos',{
      method:"POST",
      headers: {'Content-Type': 'application/json' },
      body: JSON.stringify(data)
    })

   if (response.status === 200) {
    console.log('worked')
    setShowModal(false)
    getData()

   }
  }catch(err ){
console.error(err)
  }
}
  

  const handleChange = (e) =>{
  
    const {name, value} = e.target
    setData(data =>({
      ...data,
      [name] : value

    }))
    console.log(data)

  }

    return (
      <div className="absolute left-0 top-0 w-screen  h-screen bg-black bg-opacity-50 flex justify-center items-center ">
        <div className ="w-1/2 p-[40px] bg-white shadow-custom rounded-md ">
          <div className="flex space-x-40"> 

            <h3 className="bold-font border-none bg-transparent ">Let's {mode} you task </h3>
            <button className="border-none bg-transparent hover:bg-red-700 " onClick={() => setShowModal(false)} >x</button>

          </div>
 
          
          <form className="flex flex-col">
            <input className="py-3 px-4 my-3 rounded-lg border border-solid border-green-400  "
            
            required 
            maxLength={30}
            placeholder="new task goes here"
            name="title"
            value={data.title} 
            onChange={handleChange}/>
             <br/>

             <label for = "range"> Drag to select your current progress </label>

          
            <input
            
            required
            type="range"
            id= "range"
            min="0"
            max="100"
            name="progress"
            value={data.progress}
            onChange={handleChange}
            /> 
           
            <input className= "px-2 py-1 text-xs rounded-lg bg-transparent border border-gray-400 hover:bg-green-700" type="submit" onClick = {editMode ? '': postData}/> 

          </form>
          
          </div>  
      </div>
    )
  }
  
  export default Modal
