import TickIcon  from './TickIcon' 
import ProgressBar from './ProgressBar'
import {useState} from 'react'

const ListItem = ({ task }) => {

  const  [showModal, setShowModal] = usesState(false)
  return (
    <li className="w-full my-2.5 flex rounded-lg shadow justify-between">
     
    <div className="flex items-center">
      <TickIcon/>
      <p className="text-xl">{task.title}</p>
      <ProgressBar/>
    </div>

    <div className="flex space-x-2">
  <button className="px-2 py-1 text-xs rounded-lg bg-transparent border border-gray-400 hover:bg-green-700">EDIT</button>
  <button className="px-2 py-1 text-xs rounded-lg bg-transparent border border-gray-400 hover:bg-red-700">DELETE</button>


    </div>
     {showaModal && <Modal/>}

    </li>
  );
}

export default ListItem
