import React, { useState } from 'react';
import TickIcon from './TickIcon'; // Ensure the path is correct
import ProgressBar from './ProgressBar'; // Ensure the path is correct
import Modal from './Modal'; // Ensure the path is correct

const ListItem = ({ task }) => {
  const [showModal, setShowModal] = useState(false);

  return (
    <li className="w-full my-2.5 flex rounded-lg shadow justify-between items-center">
      <div className="flex items-center space-x-4">
        <TickIcon />
        <p className="text-xl">{task.title}</p>
        <ProgressBar task={task} />
      </div>
      <div className="flex space-x-2">
        <button
          className="px-2 py-1 text-xs rounded-lg bg-transparent border border-gray-400 hover:bg-green-700"
          onClick={() => setShowModal(true)}
        >
          EDIT
        </button>
        <button className="px-2 py-1 text-xs rounded-lg bg-transparent border border-gray-400 hover:bg-red-700">
          DELETE
        </button>
      </div>
      {showModal && <Modal mode={'edit'} setShowModal={setShowModal} task={task} />}
    </li>
  );
};

export default ListItem;
