import {useState} from 'react'
import TickIcon  from './TickIcon' 
import ProgressBar from './ProgressBar'

import Modal from './ Modal'

const ListItem = ({ task }) => {

  const  [showModal, setShowModal] = useState(false)
  return (
    <li className="w-full my-2.5 flex rounded-lg shadow justify-between">
     
    <div className="flex items-center">
      <TickIcon/>
      <p className="text-xl">{task.title}</p>
      <ProgressBar/>
    </div>

    <div className="flex space-x-2">
  <button className="px-2 py-1 text-xs rounded-lg bg-transparent border border-gray-400 hover:bg-green-700" onClick={()=> setShowModal(true)}>EDIT</button>
  <button className="px-2 py-1 text-xs rounded-lg bg-transparent border border-gray-400 hover:bg-red-700">DELETE</button>


    </div>
     {showaModal && <Modal mode={'edit'} setShowModal={setShowModal}/>}

    </li>
  );
}

export default ListItem
